sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.getLocalDate() < rightnow.getLocalDate()) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate1){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow1 = new GlideDateTime();
		var testdate1 = new GlideDateTime(strDate1);

		// If the testdate is EQUAL rightnow, return true, else return false
		if (testdate1.getLocalDate() == rightnow1.getLocalDate()) {
			return true;
		}
		else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
